eventhandler.code:
if id == 0 then local dialog = guimanager:loadFromXML("te_changebound") local cmbBlueprints = Gui_findComboBox(dialog, "cmbBlueprints")
if dialog:doModal() == 0 then local bound = editor:getSelectedBound() bound:setBlueprint(cmbBlueprints:getCaption()) end elseif id == 1 then elseif id == 2 then elseif id == 3 then -- new bound blueprint local dialog = guimanager:loadFromXML("te_editbound") local edtName = Gui_findEditBox(dialog, "edtName")
if dialog:doModal() == 0 then local edtCheck = Gui_findEditBox(dialog, "edtCheck") local edtHit = Gui_findEditBox(dialog, "edtHit")
local blueprint = BoundBlueprint:new() blueprint:setName(edtName:getCaption()) blueprint:setOnShouldCheckScript(edtCheck:getCaption()) blueprint:setOnHitScript(edtHit:getCaption())
boundblueprintmanager:add(blueprint) end end
